import React, { useContext } from 'react';
import AuthContext from "../context/AuthContext";
const verifyUser = () => {
  const { verifyUserEmail } = useContext(AuthContext);
  const { uid, token,} = useParams();
  const handleSubmit = e => {
    e.preventDefault();
    const uid = uid;
    const token = token;
    loginUser(uid, token);
  };
  return (
    {verifyUserEmail}
  )
}

export default verifyUser;
