import { createContext, useState, useEffect } from "react";
import jwt_decode from "jwt-decode";
import { useHistory } from "react-router-dom";

const AuthContext = createContext();

export default AuthContext;

export const AuthProvider = ({ children }) => {
  const [authTokens, setAuthTokens] = useState(() =>
    localStorage.getItem("authTokens")
      ? JSON.parse(localStorage.getItem("authTokens"))
      : null
  );
  const [user, setUser] = useState(() =>
    localStorage.getItem("authTokens")
      ? jwt_decode(localStorage.getItem("authTokens"))
      : null
  );
  const [loading, setLoading] = useState(true);

  const history = useHistory();

  const loginUser = async (email, password) => {
    const response = await fetch("http://127.0.0.1:8000/auth/jwt/create/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email,
        password
      })
    });
    const data = await response.json();

    if (response.status === 200) {
      setAuthTokens(data);
      setUser(jwt_decode(data.access));
      localStorage.setItem("authTokens", JSON.stringify(data));
      history.push("/");
    } else if (response?.status === 400) {
          alert('Missing Username or Password');
      } else if (response?.status === 401) {
          alert('No active account found with the given credentials');
      } else {
          alert('Login Failed');
      }
  };
  const registerUser = async (email,firstname,lastname,content_category,password,re_password) => {
    const response = await fetch("http://127.0.0.1:8000/auth/users/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({

        email,
        firstname,
        lastname,
        content_category,
        password,
        re_password
      })
    }).then(response => response.json())
            .then(data => {
            if(response.status!==201)
            {
              alert(data?.email)
            }
            })
      
            if (response.status === 201) {
              history.push("/email-verification");
            }
            else if (response.status === 400)
            {
              alert("Check your email and password");
            }
             else {
              alert("Something went wrong!");
            }

  };
  const verifyUserEmail = async (uid, token) => {
    const response = await fetch("http://127.0.0.1:8000/auth/users/activation/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        uid,
        token
      }).then(response => response.json())
      .then(data => {
        console.log(data);
      })
    });
    const data = await response.json();
  };
  const logoutUser = () => {
    setAuthTokens(null);
    setUser(null);
    localStorage.removeItem("authTokens");
    history.push("/");
  };

  const contextData = {
    user,
    setUser,
    authTokens,
    setAuthTokens,
    registerUser,
    loginUser,
    logoutUser,
    verifyUserEmail
  };

  useEffect(() => {
    if (authTokens) {
      setUser(jwt_decode(authTokens.access));
    }
    setLoading(false);
  }, [authTokens, loading]);

  return (
    <AuthContext.Provider value={contextData}>
      {loading ? null : children}
    </AuthContext.Provider>
  );
};
