import React, { useState, useContext } from 'react'
import { NavLink } from "react-router-dom";
import Image from "../images/logo.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faAt, faLock, faSignature, faCameraRetro } from "@fortawesome/free-solid-svg-icons";
import "./styles/signup.css"
import "./styles/login.css"
import AuthContext from "../context/AuthContext";
export const SignUp = () => {
	const [firstname, setFirstname] = useState("");
	const [lastname, setLastname] = useState("");
	const [email, setEmail] = useState("");
	const [content_category, setCategory] = useState("");
	const [password, setPassword] = useState("");
	const [re_password, setPassword2] = useState("");
	const { registerUser } = useContext(AuthContext);

	const handleSubmit = async e => {
		e.preventDefault();
		registerUser(email, firstname, lastname, content_category, password, re_password);
	};

	return (
		<>

			<div className="container main ">
				<div className="row d-flex justify-content-start align-items-center">

					<div className="col-lg-8 offset-lg-2 col-md-8 offset-md-3 mainsignUpbox">



						<div className="loginbox" >
							<div className="text-center">
								<img className="img-fluid" src={Image} alt="Creator It" id="image" style={{ width: "50%", marginTop: "3%" }} />
							</div>
							<br />
							<h1 className="text-center" style={{ color: "#000000" }}>LogIn</h1>
							<form className="pt-4 col-md-7 mx-auto" onSubmit={handleSubmit} >

								<div class="inputContainer">
									<FontAwesomeIcon icon={faUser} id="icon" size="xl" />
									<input class="Field" required type="email" placeholder="Email" name="email" />
								</div>
								<div class="inputContainer">
									<FontAwesomeIcon icon={faLock} id="icon" size="xl" />
									<input class="Field" required type="password" placeholder="Password" name="password" />
								</div>
								<div className="d-flex jusity-content-end pb-4">
									<div className="ms-auto">
										<NavLink to="#" className="text-danger text-decoration-none">
											Forgot Password ?</NavLink>
									</div>
								</div>
								
								<br />
								<div className="d-flex align-items-center justify-content-center ">
									<button type="submit" className="btn-dark btn-lg loginBtn">Creat Account</button>
								</div>
							</form>
							<br />
						</div>

					</div>

				</div>

			</div>
		</>
	)
}
