        //     history.push('/')
        // }else{
        //     alert('Something went wrong!')
        // }
    }
    let contextData = {
        loginUser:loginUser
    }
    console.log("authcontext");
    return (
        <AuthContext.Provider value={{contextData}}>
            {children}
        </AuthContext.Provider>
    )
}
