import React from 'react';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
import { Switch, Route, Redirect } from 'react-router-dom';
import PrivateRoute from "./utils/PrivateRoute";
import { AuthProvider } from "./context/AuthContext";
import Login from "./components/Login";
import { SignUp } from "./components/SignUp";

import { Header } from './components/Header';
import { Home } from "./components/Home";
import { Profile } from "./components/Profile";
import { SubItem } from "./components/SubItem";
import { Result } from "./components/Result";
import VerifyUser from "./components/verifyUser";
import { ResetPassword } from "./components/ResetPassword";
import { EditProfile } from "./components/EditProfile";
import { Footer } from "./components/Footer";
const App = (props) => {
  const LoginContainer = () => (
    <div className="container">
      <Route exact path="/" render={() => <Redirect to="/login" />} />
      <Route exact path="/login" component={Login} />
      <Route exact path="/signup" component={SignUp} />
    </div>
  )


  const DefaultContainer = () => (
    <div>
      <Header toggleAlert={this.toggleAlert} />
      <div className="container">
        <PrivateRoute exact path="/" component={Home} />
        <Route exact path="/profile" component={Profile} />
        <Route exact path="/reset-password" component={ResetPassword} />
        <Route exact path="/edit-profile" component={EditProfile} />
        <Route exact path="/:Item" component={SubItem} />
        <Route exact path="/:Item/:Id/:SubItem/:SubId" component={Result} />

        <Route exact path="/activate/:uid/:token" component={VerifyUser} />
        <Route exact path="/email-verification" component={VerifyUser}></Route>
        <Redirect to="/" />
      </div>
      <Footer />
    </div>
  )
  return (
    <>

      <AuthProvider>

        <Switch>
        

        </Switch>
        
      </AuthProvider>
    </>
  )
}
export default App;
