import React, { useContext } from 'react';
import { NavLink } from "react-router-dom";
import loginimg from "../images/LoginImg.png"
import Image from "../images/logo.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLock,faAt } from "@fortawesome/free-solid-svg-icons";
import "./styles/login.css"
import AuthContext from "../context/AuthContext";

const Login = () => {
  const { loginUser } = useContext(AuthContext);
  const handleSubmit = e => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    loginUser(email, password);
  };

  return (
    <>
    <div className="container main ">
				<div className="row d-flex justify-content-start align-items-center">
					<div className="text-center">
						<img className="img-fluid" src={Image} alt="Creator It" id="image" />
					</div>

					<div className="col-lg-8 offset-lg-2 col-md-8 offset-md-3 mainsignUpbox">

						<div className="mt-3 mt-md-1  bg-white rounded signUpbox" >
							<h1 className="text-center" style={{ color: "#8B6CE3" }}>Sign Up</h1>
							<form className="pt-4" onSubmit={handleSubmit}>
								
								<div className="input-group flex-nowrap">
									<span className="input-group-text" id="addon-wrapping"><FontAwesomeIcon icon={faAt} size="xl" /></span>
									<input required type="email" className="form-control" style={{ fontSize: "24px" }} placeholder="Email"  id="email" aria-describedby="addon-wrapping" />
								</div>
								<br />

								
								<div className="input-group flex-nowrap">

									<span className="input-group-text" id="addon-wrapping" style={{ backgroundColor:"white"}}><FontAwesomeIcon icon={faLock} size="xl" /></span>
									<input required type="password" className="form-control" style={{ fontSize: "24px" }} placeholder="Password"  id="password" aria-describedby="addon-wrapping" />
								</div>
								<br />

							</form>
						</div>

					</div>

				</div>

			</div>
    </>
  );
};


export default Login;
